import FilterBuilderCtrl from "./filter-builder_controller.js";

function filterBuilder() {
	return {
		restrict: 'E',
		template: `<form novalidate
								ng-submit="$event.preventDefault()"
								layout="column"
								layout-align="start center"
								flex="grow"
								ng-transclude>
							</form>`,
		require: {
			$ngModel: 'ngModel'
		},
		transclude: true,
		bindToController: true,
		controller: FilterBuilderCtrl
	};
}

export default filterBuilder;

import isArray from "lodash.isarray";

function getFilter(filterName) {
  return (this.$viewValue && isArray(this.$viewValue)) ?
    this.$viewValue.length ? 
      { [this.$name]: { $in: this.$viewValue } } :
      null :
    this.$viewValue ?
      { [this.$name]: this.$viewValue } :
      null;
}

const filterInput = ['$parse', function filterInput($parse) {
	return {
		restrict: 'A',
		require: {
			NgModelCtrl: 'ngModel',
			FilterBuilderCtrl: '^filterBuilder'
		},
		link: function FilterInputLink(scope, el, attrs, ctrl) {
			if (!attrs.name) throw new Error('A filter control is missing its \'name\' attribute!');
			const { NgModelCtrl, FilterBuilderCtrl } = ctrl;
			NgModelCtrl.getFilter = getFilter;
			if (attrs.filterInput) {
				$parse(attrs.filterInput).assign(scope, () => NgModelCtrl.getFilter());
			}
			FilterBuilderCtrl.parts[attrs.ngModel] = NgModelCtrl;
		}
	};
}];

export default filterInput;

import campusMap from './map_component'
import campusMapControls from './controls/map-controls_component';

import filterBuilder from './filter/filter-builder_directive';
import filterInput from './filter/filter-input_directive';


import MAP_SETTINGS from './constants/map-settings_constant';
import MAP_ICONS from './constants/map-icons_constant';

// development only
